// Some portions generated by Codex
package org.eclipse.rdf4j.benchmark.common.plan;

import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.junit.jupiter.api.Assertions.assertTrue;

import org.eclipse.rdf4j.query.QueryLanguage;
import org.eclipse.rdf4j.query.algebra.TupleExpr;
import org.eclipse.rdf4j.query.parser.ParsedQuery;
import org.eclipse.rdf4j.query.parser.QueryParserUtil;
import org.junit.jupiter.api.Test;

class TupleExprJsonCodecTest {

	@Test
	void roundTripsTupleExprViaJsonPayload() {
		TupleExpr original = parse("SELECT ?s ?p ?o WHERE { ?s ?p ?o . FILTER(?p = ?p) }");
		TupleExprJsonCodec codec = new TupleExprJsonCodec();

		String encoded = codec.toJson(original);
		TupleExpr decoded = codec.fromJson(encoded);

		assertTrue(encoded.contains("payloadBase64"), "Expected payloadBase64 in tuple expression JSON");
		assertEquals(original.toString(), decoded.toString(), "Tuple expression tree should round-trip");
		assertEquals(codec.fingerprint(original), codec.fingerprint(decoded), "Fingerprint should stay stable");
	}

	private static TupleExpr parse(String query) {
		ParsedQuery parsedQuery = QueryParserUtil.parseQuery(QueryLanguage.SPARQL, query, null);
		return parsedQuery.getTupleExpr();
	}
}
